<template>
    <div class="gm-lazyimg">
        <div :style="style">
            <img :src="src" @load="onload" />
        </div>
    </div>
</template>

<script>
export default {
    props: {
        // 图片 height/width 比例
        ratio: {
            type: Number,
            default: 0.4
        },
        src: {
            type: String,
            default: ''
        }
    },

    data () {
        return {
            pdBtom: `${100 * this.ratio}%`
        }
    },

    computed: {
        style: function () {
            return {
                'padding-bottom': this.pdBtom,
                'height': this.pdBtom ? 0 : 'auto',
                'overflow': this.pdBtom ? 'hidden' : 'visible'
            }
        }
    },

    methods: {
        onload: function () {
            this.pdBtom = 0
        }
    }
}
</script>

<style lang="scss" scoped>
.gm-lazyimg > div, .gm-lazyimg img {
    width: 100%;
}
.gm-lazyimg > div {
    font-size: 0;
    height: 0;
    display: inline-block;
    vertical-align: middle;
    overflow: hidden;
    background: #F7F7F7 url("data:image/jpg;base64,") no-repeat 50% 50%;
    background-size: 40% auto;
}
</style>
